var vid;var playbtn;var playbtn;var clickplay;var slider;var current;var duration;var mutebtn;var fullscreenbtn;function intializePlayer(){vid=document.getElementById("mainVideo");
playbtn=document.getElementById("playPausebtn");clickplay=document.getElementById("mainVideo");slider=document.getElementById("slider");current=document.getElementById("current");
duration=document.getElementById("duration");mutebtn=document.getElementById("mutebtn");fullscreenbtn=document.getElementById("fullscreenbtn");playbtn.addEventListener("click",playPause,false);
clickplay.addEventListener("click",clickPlay,false);slider.addEventListener("change",vidSeek,false);vid.addEventListener("timeupdate",seektimeupdate,false);
mutebtn.addEventListener("click",vidmute,false);fullscreenbtn.addEventListener("click",toggleFullScreen,false);}window.onload=intializePlayer;function playPause(){if(vid.paused){vid.play();
playbtn.innerHTML="<i class='icon-pause'></i>";}else{vid.pause();playbtn.innerHTML="<i class='icon-play'></i>";}}function clickPlay(){if(vid.paused){vid.play();
playbtn.innerHTML="<i class='icon-pause'></i>";}else{vid.pause();playbtn.innerHTML="<i class='icon-play'></i>";}}function vidSeek(){var a=vid.duration*(slider.value/100);
vid.currentTime=a;}function seektimeupdate(){var a=vid.currentTime*(100/vid.duration);slider.value=a;var d=Math.floor(vid.currentTime/60);var b=Math.floor(vid.currentTime-d*60);
var e=Math.floor(vid.duration/60);var c=Math.floor(vid.duration-e*60);if(b<10){b="0"+b;}if(c<10){c="0"+c;}if(d<10){d="0"+d;}if(e<10){e="0"+e;}current.innerHTML=d+":"+b;
duration.innerHTML=e+":"+c;}function vidmute(){if(vid.muted){vid.muted=false;mutebtn.innerHTML="<i class='icon-unmute'></i>";}else{vid.muted=true;mutebtn.innerHTML="<i class='icon-mute'></i>";
}}function toggleFullScreen(){if(vid.requestFullScreen){vid.requestFullScreen();}else{if(vid.webkitRequestFullScreen){vid.webkitRequestFullScreen();}else{if(vid.mozRequestFullScreen){vid.mozRequestFullScreen();
}}}}function touchStart(b){var a=b.target;}